import type { u32 } from '@polkadot/types';
import type { AccountId, Balance, BalanceOf, BlockNumber, StrikeCount } from '@polkadot/types/interfaces';
export interface DeriveSociety {
    bids: any[];
    defender?: AccountId;
    hasDefender: boolean;
    head?: AccountId;
    founder?: AccountId;
    maxMembers: u32;
    pot: BalanceOf;
}
export interface DeriveSocietyCandidate {
    accountId: AccountId;
    kind: any;
    value: Balance;
    isSuspended: boolean;
}
export interface DeriveSocietyMember {
    accountId: AccountId;
    isDefenderVoter: boolean;
    isSuspended: boolean;
    payouts: [BlockNumber, Balance][];
    strikes: StrikeCount;
    vote?: any;
    vouching?: any;
}
